mapper.insert:
insert into tb_address (id, uid, address, 
  is_default_addr, receiver, tel
  )
values (null, #{uid,jdbcType=INTEGER}, #{address,jdbcType=VARCHAR}, 
  #{isDefaultAddr,jdbcType=CHAR}, #{receiver,jdbcType=VARCHAR}, #{tel}
  )

mapper.update:
update tb_address
set    is_default_addr ="0"
where uid = #{uid,jdbcType=INTEGER}

update tb_address
set    is_default_addr ="1"
where uid = #{uid,jdbcType=INTEGER}
and id = #{id,jdbcType=INTEGER}

mapper.select:
select id, uid, address, is_default_addr, receiver, tel
from tb_address
where id = #{id,jdbcType=INTEGER}
